// ToReadList.js
import React, { useState, useEffect } from 'react';

const ToReadList = ({ username }) => {
    const [toReadList, setToReadList] = useState([]);

    useEffect(() => {
        // Fetch initial To Read list
        fetch('/to-read')
            .then(response => response.json())
            .then(data => setToReadList(data.toReadList))
            .catch(error => console.error('Error:', error));

        // Setup SSE connection
        const eventSource = new EventSource('/to-read-updates');
        eventSource.onmessage = (event) => {
            const eventData = JSON.parse(event.data);
            if (eventData.type === 'update') {
                setToReadList(eventData.toReadList);
            }
        };

        return () => {
            // Clean up SSE connection
            eventSource.close();
        };
    }, []);

    return (
        <div>
            <h2>Welcome, {username}!</h2>
            <h3>To Read List:</h3>
            <ul>
                {toReadList.map((book, index) => (
                    <li key={index}>{book}</li>
                ))}
            </ul>
        </div>
    );
};

export default ToReadList;
